ma.viewersText:
شاهده الآن في المتصفح <span class='viewer-count'>230</span><script>// تعريف المتغيرات
let viewers = 230; // العدد الابتدائي
const minViewers = 150; // العدد الأدنى
const decrementStep = 1; // خطوة الإنقاص
const delay = 1000; // زمن التأخير بالمللي ثانية (1 ثانية)

function updateViewerCount() {
    const viewerElement = document.querySelector('.viewer-count');
    if (viewerElement) {
        viewers -= decrementStep;
        viewerElement.textContent = viewers;

        if (viewers <= minViewers) {
            viewers = 230; // إعادة التعيين إلى العدد الابتدائي
        }
    }
}

// تكرار العملية كل ثانية
setInterval(updateViewerCount, delay);</script> شخص.
